;(function() {
'use strict';
/**
 * Convert a template string into HTML DOM nodes
 * @param  {String} str The template string
 * @return {Node}       The template HTML
 */

var stringToHTML = function stringToHTML(str) {
  var dom = document.createElement('section');
  dom.classList.add('dates');
  dom.innerHTML = str;
  return dom;
};

var main = document.querySelector('main');
var info = stringToHTML(JSON.parse(localStorage.fragment));
main.appendChild(info);
document.querySelectorAll('.date-info__text').forEach(function (item) {
  item.style.whiteSpace = 'pre-line';
});
}());
